                <div class=\"toggle-container-right\">
                    <i class=\"fa fa-chevron-circle-left\" aria-hidden=\"true\" id=\"toggleLeft\"></i></div>
            </div>
            <div class=\"rightTreeSideRightInner\">
                {% if editorRights %}
