            <script>
                window.firebase = firebase;
                window.firebaseConfig = {
                    apiKey: \"{{ firebase_api_key |e('js') }}\",
                    authDomain: \"{{ firebase_auth_domain |e('js') }}\",
                    databaseURL: \"{{ firebase_db_url |e('js') }}\"
                };
            </script>
        {% endif %}

