{% block title %}{{ lsDocTitle }}{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    {% if 'comments' is active feature %}
        <link rel=\"stylesheet\" href=\"{{ asset('build/commentscss.css', 'encore') }}\"/>
