                            <button type=\"button\" class=\"changeTree2DocumentBtn btn btn-primary btn-xs pull-right\">
                                Change Document
                            </button>
                            Drag and drop from right to left to <b>create associations</b>.
                        </div>

                        <div id=\"tree2SelectorDiv\" class=\"container-fluid\">
                            <div class=\"row\">
                                {{ form_widget(docList) }}
