                        </div>
                        <div class=\"modal-body\">
                            <div class=\"contentModal\">
                                <div class=\"row\">
                                    <div class=\"col-sm-1\"></div>
                                    <div class=\"col-sm-4\">
                                        <select class=\"form-control\" disabled>
                                            <option>{{ lsDocTitle }}</option>
                                        </select>
                                    </div>
                                    <div class=\"col-sm-2\">
                                        <div class=\"btn-group\" role=\"group\" aria-label=\"\">
                                            <a id=\"copyFrameworkModal_copyLeftBtn\" href=\"#\" class=\"btn btn-default\" data-toggle=\"tooltip\" data-placement=\"bottom\" title=\"Copy From\" role=\"button\">
                                                <i class=\"fa fa-arrow-left fa-lg\"></i>
                                            </a>
                                            <a id=\"copyFrameworkModal_copyRightBtn\" href=\"#\" class=\"btn btn-default active\" data-toggle=\"tooltip\" data-placement=\"bottom\" title=\"Copy To\" role=\"button\">
                                                <i class=\"fa fa-arrow-right fa-lg\"></i>
                                            </a>
                                        </div>
                                    </div>
                                    <div class=\"col-sm-4\">
                                        {% set lastCreator = '' %}
                                        <select id=\"js-framework-to-copy\" class=\"form-control\" name=\"frameworkToCopy\">
                                            {% for framework in lsDocs %}
                                                {% if framework.adoptionStatus not in [ 'Adopted', 'Deprecated' ] %}
                                                    {% if framework.creator != lastCreator %}
                                                        <optgroup label=\"{{ framework.creator }}\">
                                                        {% set lastCreator = framework.creator %}
                                                    {% endif %}
                                                    {% if framework.title != lsDocTitle %}
                                                        <option value=\"{{ framework.id }}\">{{ framework.title }}</option>
                                                    {% endif %}
                                                    {% if loop.last == true and framework.creator != '' %}
                                                        </optgroup>
                                                    {% endif %}
                                                {% endif %}
                                            {% endfor %}
                                        </select>
                                    </div>
                                    <div class=\"col-sm-1\"></div>
                                </div>
                                <div class=\"row\">
                                    <div class=\"col-sm-3\"></div>
                                    <div class=\"col-sm-7\">
                                        <div class=\"radio\">
                                          <label>
                                            <input type=\"radio\" name=\"type\" id=\"copyType\" value=\"copy\" aria-label=\"Copy Content\" checked>
                                            Copy Content <small>(new identifiers, no associations copied)</small>
                                          </label>
                                        </div>
                                        <div class=\"radio\">
                                          <label>
