            apx.path.lsdoc_delete = '{{ path('lsdoc_delete', {'id':'ID'}) }}';
            apx.path.lsdoc_unlock = '{{ path('lsdoc_unlock', {'id':'ID'}) }}';
            apx.path.lsdoc_lock = '{{ path('lsdoc_lock', {'id':'ID'}) }}';
