            //////////////////////////////////
            function initialize() {
                if (!\$.isFunction(apx.initialize)) {
                    setTimeout(initialize, 100);
                    return;
                }
                apx.initialize();
            }

            \$(function () {
                initialize();
            });
        </script>
        {% endautoescape %}
    {% endblock %}
", "framework/doc_tree/view.html.twig", "/var/www/html/templates/framework/doc_tree/view.html.twig");
